let city=document.querySelector(".header__inner-top__city-location"),modal=document.querySelector(".header__inner-top__city-modal"),clearInput=document.querySelector(".header__inner-top__city-modal__clear");function inserMark(e,t,n){return e.slice(0,t)+"<mark>"+e.slice(t,t+n)+"</mark>"+e.slice(t+n)}document.querySelector(".header__inner-top__city-modal__settlement").oninput=function(){let e=this.value.trim(),t=document.querySelectorAll(".header__inner-top__city-modal__item");""!==e?t.forEach((t=>{if(-1===t.innerText.search(e))t.classList.add("hide"),t.innerHTML=t.innerText;else{t.classList.remove("hide");let n=t.innerText;t.innerHTML=inserMark(n,t.innerText.search(e),e.length)}})):t.forEach((e=>{e.classList.remove("hide"),e.innerHTML=e.innerText}))};const onShowModal=()=>{modal.classList.toggle("toggle")};city.onclick=onShowModal;